INSERT INTO `illustrator` (`id`, `name`, `image_url`, `description`) VALUES (1, 'Mark Schultz', 'https://www.sdcomicfest.org/images/mark-schultz-by-mark-thelosen.jpg', 'Mark Schultz (born June 7, 1955) is an American writer and illustrator of books and comics. Schultz’s first published comics work was on a story called “The Sea King”, featuring Robert E. Howard’s character King Kull, which appeared in Savage Sword of Conan #132, published by Marvel Comics. In 2002, Schultz contributed a number of illustrations to Conan the Cimmerian: Volume 1, a new reprinting of the Conan stories of Robert E. Howard, published by Wandering Star Books. The book has since been reprinted in paperback by Del Rey as The Coming of Conan the Cimmerian.');
INSERT INTO `illustrator` (`id`, `name`, `image_url`, `description`) VALUES (2, 'Gary Gianni', 'https://pbs.twimg.com/profile_images/713054373098729472/hg0GYX03_400x400.jpg', 'Gary Gianni (born 1954) is an American comics artist best known for his eight years illustrating the syndicated newspaper comic Prince Valiant. Gianni provided the cover and interior art for the second of Wandering Star\'s Conan collections, Conan of Cimmeria: Volume Two (1934) (2004), also published by Del Rey as The Bloody Crown of Conan (2003, 2005). (He also illustrated two of Wandering Star\'s other definitive Robert E. Howard collections, The Savage Tales of Solomon Kane (1998) and Bran Mak Morn: The Last King, both also published by Del Rey.)');
INSERT INTO `illustrator` (`id`, `name`, `image_url`, `description`) VALUES (3, 'Justin Sweet', 'https://cdnb.artstation.com/p/users/avatars/000/146/925/large/9ff35502b63c2d448361060b64e6f2c7.jpg', 'A concept artist, illustrator and gallery artist. He also works for various game and book publishers, including production companies. He is a well known and respected fantasy artist and some of his clients include Disney, National Geographic, Wizards of the Coast, Tor, DelRey, and Wandering Star. Justin also did concept work for Magic: The Gathering, Avengers, Guardians of the Universe, John Carter, Prince Caspian, and Game of Thrones.');
INSERT INTO `illustrator` (`id`, `name`, `image_url`, `description`) VALUES (4, 'Jim Keegan', 'https://images.squarespace-cdn.com/content/v1/6232a326973c4550cddc63e0/39164cd0-9f1c-41fc-99ea-65e8f57d7a67/IMG_5879.jpeg', 'Jim & Ruth Keegan are award-winning collaborative artists who have illustrated books by Stephen King, Robert E. Howard, Fritz Leiber, William F. Nolan, George Clayton Johnson, and many others. Their clients include Penguin Random House, Bantam Books, Del Rey Books, Dark Horse Comics, Columbia Tri-Star, The CW Network, Carsey-Warner, Playboy Enterprises, Centipede Press, The Robert. E. Howard Foundation, Wandering Star Books, and Montgomery Studios.');
INSERT INTO `illustrator` (`id`, `name`, `image_url`, `description`) VALUES (5, 'Ruth Keegan', 'https://reh.world/wp-content/uploads/2021/09/jimruth2_web.png', 'Jim & Ruth Keegan are award-winning collaborative artists who have illustrated books by Stephen King, Robert E. Howard, Fritz Leiber, William F. Nolan, George Clayton Johnson, and many others. Their clients include Penguin Random House, Bantam Books, Del Rey Books, Dark Horse Comics, Columbia Tri-Star, The CW Network, Carsey-Warner, Playboy Enterprises, Centipede Press, The Robert. E. Howard Foundation, Wandering Star Books, and Montgomery Studios.');
INSERT INTO `illustrator` (`id`, `name`, `image_url`, `description`) VALUES (6, 'Gregory Manchess', 'https://www.24-7pressrelease.com/assets/attachments/048/press_release_distribution_0487109_175641.jpg', 'The award-winning painter Gregory Manchess has worked as a freelance illustrator for nearly forty years. His work appeared in Conan of Cimmeria: Volume Three (1935-1936), a collection of Robert E. Howard\'s Conan stories, published by Wandering Star, and published by Del Rey as The Conquering Sword of Conan.');
INSERT INTO `illustrator` (`id`, `name`, `image_url`, `description`) VALUES (7, 'Greg Staples', 'https://static.wikia.nocookie.net/tales-from-the-tower/images/7/78/Greg_Staples_-_International_Comic_Expo_-_2015.jpg', 'Greg Staples (born May 27, 1970 in Sheffield) is a British comic book artist and illustrator, credited as illustrating The Horror Stories of Robert E. Howard, a collection of Howard\'s weird fiction, including many of his contributions to the Cthulhu Mythos, published by Del Rey/Ballantine in 2008.');
INSERT INTO `illustrator` (`id`, `name`, `image_url`, `description`) VALUES (8, 'Tim Bradstreet', 'https://upload.wikimedia.org/wikipedia/commons/1/10/10.18.09TimBradstreetByLuigiNovi.jpg', 'Timothy \"Tim\" Bradstreet is an artist and illustrator, best known for his work on comic books, book covers, movie posters, roleplaying games, and trading cards. He is credited as the illustrator for the collection of Robert E. Howard’s El Borak and Other Desert Adventures. This book focuses on Howard’s Central Asian stories featuring eleven stories for which Bradstreet contributed 70 pen and ink drawings.');
INSERT INTO `illustrator` (`id`, `name`, `image_url`, `description`) VALUES (9, 'John Watkiss', 'https://upload.wikimedia.org/wikipedia/commons/3/31/John-Watkiss_rkw4387.gif', 'John Watkiss (28 July 1961 – 20 January 2017) was a British artist, known for his painting and his work in comics and film production. His career led him from artist for graphic novels to storyboard artist and character designer. He is arguably best known for his visual development work on Tarzan. He is credited as the illustrator for the collection of Robert E. Howard’s Sword Woman and Other Historical Adventures.');
INSERT INTO `illustrator` (`id`, `name`, `image_url`, `description`) VALUES (DEFAULT, NULL, NULL, NULL);
